import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useForm } from '../../contexts/FormContext';
import { useAuth } from '../../contexts/AuthContext';
import FormField from '../form/FormField';
import { 
  ArrowLeft, 
  Plus, 
  Save, 
  Eye, 
  EyeOff, 
  Type, 
  Mail, 
  Hash, 
  AlignLeft, 
  List, 
  Circle, 
  CheckSquare, 
  Calendar,
  Settings
} from 'lucide-react';

const FormBuilder = () => {
  const { formId } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { getFormById, createForm, updateForm, loading } = useForm();
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    fields: []
  });
  const [showPreview, setShowPreview] = useState(false);
  const [previewValues, setPreviewValues] = useState({});
  const [isEditing, setIsEditing] = useState(false);

  const fieldTypes = [
    { type: 'text', label: 'Text Input', icon: Type },
    { type: 'email', label: 'Email Input', icon: Mail },
    { type: 'number', label: 'Number Input', icon: Hash },
    { type: 'textarea', label: 'Text Area', icon: AlignLeft },
    { type: 'select', label: 'Dropdown', icon: List },
    { type: 'radio', label: 'Radio Buttons', icon: Circle },
    { type: 'checkbox', label: 'Checkboxes', icon: CheckSquare },
    { type: 'date', label: 'Date Picker', icon: Calendar }
  ];

  useEffect(() => {
    const loadForm = async () => {
      if (formId) {
        const existingForm = await getFormById(formId);
        if (existingForm) {
          setFormData(existingForm);
          setIsEditing(true);
        }
      }
    };
    
    loadForm();
  }, [formId, getFormById]);

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(formData.fields);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setFormData(prev => ({ ...prev, fields: items }));
  };

  const addField = (fieldType) => {
    const newField = {
      id: Date.now().toString(),
      type: fieldType,
      label: `New ${fieldType} field`,
      required: false,
      options: fieldType === 'select' || fieldType === 'radio' || fieldType === 'checkbox' 
        ? ['Option 1', 'Option 2', 'Option 3'] 
        : undefined
    };

    setFormData(prev => ({
      ...prev,
      fields: [...prev.fields, newField]
    }));
  };

  const updateField = (index, updatedField) => {
    setFormData(prev => ({
      ...prev,
      fields: prev.fields.map((field, i) => 
        i === index ? updatedField : field
      )
    }));
  };

  const deleteField = (index) => {
    setFormData(prev => ({
      ...prev,
      fields: prev.fields.filter((_, i) => i !== index)
    }));
  };

  const handleSave = async () => {
    if (!formData.title.trim()) {
      alert('Please enter a form title');
      return;
    }

    if (formData.fields.length === 0) {
      alert('Please add at least one field to the form');
      return;
    }

    try {
      const formDataWithCreator = {
        ...formData,
        createdBy: currentUser.id
      };

      if (isEditing) {
        await updateForm(formId, formDataWithCreator);
      } else {
        const result = await createForm(formDataWithCreator);
        if (result.success) {
          navigate('/admin');
        }
      }
    } catch (error) {
      console.error('Error saving form:', error);
    }
  };

  const handlePreviewChange = (index, value) => {
    setPreviewValues(prev => ({
      ...prev,
      [index]: value
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/admin')}
                className="flex items-center text-gray-600 hover:text-gray-900"
              >
                <ArrowLeft className="h-5 w-5 mr-2" />
                Back to Dashboard
              </button>
              <h1 className="text-xl font-semibold text-gray-900">
                {isEditing ? 'Edit Form' : 'Create New Form'}
              </h1>
            </div>
            
            <div className="flex items-center space-x-3">
              <button
                onClick={() => setShowPreview(!showPreview)}
                className="flex items-center px-3 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                {showPreview ? <EyeOff className="h-4 w-4 mr-2" /> : <Eye className="h-4 w-4 mr-2" />}
                {showPreview ? 'Hide Preview' : 'Show Preview'}
              </button>
              
              <button
                onClick={handleSave}
                disabled={loading}
                className="flex items-center px-4 py-2 text-sm font-medium text-white bg-primary-600 border border-transparent rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50"
              >
                {loading ? (
                  <div className="loading-spinner mr-2"></div>
                ) : (
                  <Save className="h-4 w-4 mr-2" />
                )}
                {isEditing ? 'Update Form' : 'Save Form'}
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Form Settings */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
                <Settings className="h-5 w-5 mr-2" />
                Form Settings
              </h2>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Form Title *
                  </label>
                  <input
                    type="text"
                    value={formData.title}
                    onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    placeholder="Enter form title"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    value={formData.description}
                    onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent resize-vertical"
                    placeholder="Enter form description"
                  />
                </div>
              </div>
            </div>

            {/* Field Types */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mt-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
                <Plus className="h-5 w-5 mr-2" />
                Add Fields
              </h3>
              
              <div className="grid grid-cols-2 gap-3">
                {fieldTypes.map((fieldType) => {
                  const Icon = fieldType.icon;
                  return (
                    <button
                      key={fieldType.type}
                      onClick={() => addField(fieldType.type)}
                      className="flex flex-col items-center p-3 border border-gray-200 rounded-lg hover:border-primary-300 hover:bg-primary-50 transition-colors"
                    >
                      <Icon className="h-6 w-6 text-gray-600 mb-2" />
                      <span className="text-xs text-gray-700 text-center">
                        {fieldType.label}
                      </span>
                    </button>
                  );
                })}
              </div>
            </div>
          </div>

          {/* Form Builder Area */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <div className="p-6 border-b border-gray-200">
                <h2 className="text-lg font-medium text-gray-900">
                  {showPreview ? 'Form Preview' : 'Form Builder'}
                </h2>
                <p className="text-sm text-gray-600 mt-1">
                  {showPreview 
                    ? 'Preview how your form will appear to users'
                    : 'Drag and drop fields to build your form'
                  }
                </p>
              </div>

              <div className="p-6">
                {showPreview ? (
                  /* Preview Mode */
                  <div className="max-w-2xl mx-auto">
                    <div className="mb-6">
                      <h1 className="text-2xl font-bold text-gray-900 mb-2">
                        {formData.title || 'Untitled Form'}
                      </h1>
                      {formData.description && (
                        <p className="text-gray-600">{formData.description}</p>
                      )}
                    </div>

                    {formData.fields.map((field, index) => (
                      <FormField
                        key={field.id || index}
                        field={field}
                        index={index}
                        isPreview={true}
                        value={previewValues[index] || ''}
                        onChange={(value) => handlePreviewChange(index, value)}
                      />
                    ))}

                    <div className="mt-8 pt-6 border-t border-gray-200">
                      <button className="w-full bg-primary-600 text-white py-3 px-4 rounded-md hover:bg-primary-700 transition-colors">
                        Submit Form
                      </button>
                    </div>
                  </div>
                ) : (
                  /* Builder Mode */
                  <div>
                    {formData.fields.length === 0 ? (
                      <div className="text-center py-12">
                        <div className="mx-auto h-12 w-12 text-gray-400 mb-4">
                          <Plus className="h-12 w-12" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900 mb-2">
                          No fields added yet
                        </h3>
                        <p className="text-gray-600 mb-4">
                          Start building your form by adding fields from the sidebar
                        </p>
                      </div>
                    ) : (
                      <DragDropContext onDragEnd={handleDragEnd}>
                        <Droppable droppableId="form-fields">
                          {(provided) => (
                            <div
                              {...provided.droppableProps}
                              ref={provided.innerRef}
                              className="space-y-4"
                            >
                              {formData.fields.map((field, index) => (
                                <Draggable
                                  key={field.id || index}
                                  draggableId={field.id || index.toString()}
                                  index={index}
                                >
                                  {(provided, snapshot) => (
                                    <div
                                      ref={provided.innerRef}
                                      {...provided.draggableProps}
                                      {...provided.dragHandleProps}
                                      className={`${snapshot.isDragging ? 'dragging' : ''}`}
                                    >
                                      <FormField
                                        field={field}
                                        index={index}
                                        onUpdate={updateField}
                                        onDelete={deleteField}
                                      />
                                    </div>
                                  )}
                                </Draggable>
                              ))}
                              {provided.placeholder}
                            </div>
                          )}
                        </Droppable>
                      </DragDropContext>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FormBuilder;
