import React, { createContext, useContext, useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import toast from 'react-hot-toast';
import databaseService from '../services/database';

const FormContext = createContext();

export const useForm = () => {
  const context = useContext(FormContext);
  if (!context) {
    throw new Error('useForm must be used within a FormProvider');
  }
  return context;
};

export const FormProvider = ({ children }) => {
  const [forms, setForms] = useState([]);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(false);

  // Load data from database on mount
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [formsData, responsesData] = await Promise.all([
        databaseService.getAllForms(),
        databaseService.getFormResponses() // This will be empty initially
      ]);
      
      setForms(formsData);
      setResponses(responsesData);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const createForm = async (formData) => {
    setLoading(true);
    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const result = await databaseService.createForm(formData, formData.createdBy);
      
      if (result.success) {
        // Reload forms to get the updated list
        const updatedForms = await databaseService.getAllForms();
        setForms(updatedForms);
        toast.success('Form created successfully!');
        return { success: true, formId: result.formId };
      }
    } catch (error) {
      toast.error('Failed to create form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };

  const updateForm = async (formId, formData) => {
    setLoading(true);
    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      await databaseService.updateForm(formId, formData);
      
      // Reload forms to get the updated list
      const updatedForms = await databaseService.getAllForms();
      setForms(updatedForms);
      
      toast.success('Form updated successfully!');
      return { success: true };
    } catch (error) {
      toast.error('Failed to update form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };

  const deleteForm = async (formId) => {
    setLoading(true);
    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      await databaseService.deleteForm(formId);
      
      // Reload forms to get the updated list
      const updatedForms = await databaseService.getAllForms();
      setForms(updatedForms);
      
      toast.success('Form deleted successfully!');
      return { success: true };
    } catch (error) {
      toast.error('Failed to delete form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };

  const submitResponse = async (formId, responseData, userId) => {
    setLoading(true);
    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      await databaseService.submitResponse(formId, userId, responseData);
      
      // Reload responses to get the updated list
      const updatedResponses = await databaseService.getFormResponses(formId);
      setResponses(prev => {
        const filtered = prev.filter(r => r.formId !== formId);
        return [...filtered, ...updatedResponses];
      });
      
      toast.success('Response submitted successfully!');
      return { success: true };
    } catch (error) {
      toast.error(error.message || 'Failed to submit response');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };

  const getFormById = async (formId) => {
    try {
      return await databaseService.getFormById(formId);
    } catch (error) {
      console.error('Error getting form by ID:', error);
      return null;
    }
  };

  const getFormResponses = async (formId) => {
    try {
      return await databaseService.getFormResponses(formId);
    } catch (error) {
      console.error('Error getting form responses:', error);
      return [];
    }
  };

  const getUserResponses = async (userId) => {
    try {
      return await databaseService.getUserResponses(userId);
    } catch (error) {
      console.error('Error getting user responses:', error);
      return [];
    }
  };

  const getActiveForms = async () => {
    try {
      return await databaseService.getActiveForms();
    } catch (error) {
      console.error('Error getting active forms:', error);
      return [];
    }
  };

  const getFormResponseCount = async (formId) => {
    try {
      return await databaseService.getFormResponseCount(formId);
    } catch (error) {
      console.error('Error getting form response count:', error);
      return 0;
    }
  };

  const hasUserResponded = async (formId, userId) => {
    try {
      return await databaseService.hasUserResponded(formId, userId);
    } catch (error) {
      console.error('Error checking user response:', error);
      return false;
    }
  };

  const getStatistics = async () => {
    try {
      return await databaseService.getStatistics();
    } catch (error) {
      console.error('Error getting statistics:', error);
      return { totalUsers: 0, totalForms: 0, totalResponses: 0, activeForms: 0 };
    }
  };

  const value = {
    forms,
    responses,
    loading,
    createForm,
    updateForm,
    deleteForm,
    submitResponse,
    getFormById,
    getFormResponses,
    getUserResponses,
    getActiveForms,
    getFormResponseCount,
    hasUserResponded,
    getStatistics,
    loadData
  };

  return (
    <FormContext.Provider value={value}>
      {children}
    </FormContext.Provider>
  );
};
