import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import socialAuthService from '../../services/socialAuth';
import { Chrome, Facebook } from 'lucide-react';

const SocialLogin = () => {
  const [isLoading, setIsLoading] = useState({ google: false, facebook: false });
  const { loginWithSocial } = useAuth();
  const navigate = useNavigate();

  const handleSocialLogin = async (provider) => {
    try {
      setIsLoading(prev => ({ ...prev, [provider]: true }));

      // For demo purposes, use the demo login
      // In production, you would use the OAuth popup flow
      const result = await socialAuthService.demoSocialLogin(provider);
      
      if (result.success) {
        // Login the user through the auth context
        await loginWithSocial(result.user);
        
        // Navigate based on user role
        if (result.user.role === 'admin') {
          navigate('/admin');
        } else {
          navigate('/user');
        }
      }
    } catch (error) {
      console.error(`${provider} login error:`, error);
    } finally {
      setIsLoading(prev => ({ ...prev, [provider]: false }));
    }
  };

  const handleOAuthLogin = async (provider) => {
    try {
      setIsLoading(prev => ({ ...prev, [provider]: true }));

      let config;
      switch (provider) {
        case 'google':
          config = socialAuthService.getGoogleConfig();
          break;
        case 'facebook':
          config = socialAuthService.getFacebookConfig();
          break;
        default:
          throw new Error(`Unsupported provider: ${provider}`);
      }

      const result = await socialAuthService.openOAuthPopup(provider, config);
      
      if (result.success) {
        await loginWithSocial(result.user);
        
        if (result.user.role === 'admin') {
          navigate('/admin');
        } else {
          navigate('/user');
        }
      }
    } catch (error) {
      console.error(`${provider} OAuth error:`, error);
    } finally {
      setIsLoading(prev => ({ ...prev, [provider]: false }));
    }
  };

  return (
    <div className="space-y-3">
      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <div className="w-full border-t border-gray-300" />
        </div>
        <div className="relative flex justify-center text-sm">
          <span className="px-2 bg-white text-gray-500">Or continue with</span>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-3">
        {/* Google Login Button */}
        <button
          onClick={() => handleSocialLogin('google')}
          disabled={isLoading.google}
          className="w-full inline-flex justify-center items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isLoading.google ? (
            <div className="loading-spinner h-4 w-4"></div>
          ) : (
            <>
              <Chrome className="h-4 w-4 mr-2 text-red-600" />
              Google
            </>
          )}
        </button>

        {/* Facebook Login Button */}
        <button
          onClick={() => handleSocialLogin('facebook')}
          disabled={isLoading.facebook}
          className="w-full inline-flex justify-center items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isLoading.facebook ? (
            <div className="loading-spinner h-4 w-4"></div>
          ) : (
            <>
              <Facebook className="h-4 w-4 mr-2 text-blue-600" />
              Facebook
            </>
          )}
        </button>
      </div>

      {/* Demo Note */}
      <div className="bg-blue-50 border border-blue-200 rounded-md p-3">
        <div className="text-xs text-blue-800">
          <p className="font-medium mb-1">Demo Note:</p>
          <p>This demo uses simulated social login. In production, this would open OAuth popup windows for real Google and Facebook authentication.</p>
        </div>
      </div>
    </div>
  );
};

export default SocialLogin;
