import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useForm } from '../../contexts/FormContext';
import UserProfile from '../auth/UserProfile';
import { 
  LogOut, 
  FileText, 
  Users, 
  Calendar,
  CheckCircle,
  Clock,
  ArrowRight
} from 'lucide-react';

const UserDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, logout } = useAuth();
  const { getActiveForms, getFormResponseCount, hasUserResponded } = useForm();
  const [activeForms, setActiveForms] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadActiveForms();
  }, []);

  const loadActiveForms = async () => {
    try {
      setLoading(true);
      const forms = await getActiveForms();
      
      // Get response counts and user response status for each form
      const formsWithData = await Promise.all(
        forms.map(async (form) => {
          const [responseCount, hasResponded] = await Promise.all([
            getFormResponseCount(form.id),
            hasUserResponded(form.id, currentUser.id)
          ]);
          
          return {
            ...form,
            responseCount,
            hasResponded
          };
        })
      );
      
      setActiveForms(formsWithData);
    } catch (error) {
      console.error('Error loading active forms:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getFormStatus = (form) => {
    return form.hasResponded ? 'completed' : 'pending';
  };

  const getStatusIcon = (status) => {
    if (status === 'completed') {
      return <CheckCircle className="h-5 w-5 text-green-600" />;
    }
    return <Clock className="h-5 w-5 text-yellow-600" />;
  };

  const getStatusText = (status) => {
    if (status === 'completed') {
      return 'Completed';
    }
    return 'Pending';
  };

  const getStatusColor = (status) => {
    if (status === 'completed') {
      return 'bg-green-100 text-green-800';
    }
    return 'bg-yellow-100 text-yellow-800';
  };

  const completedForms = activeForms.filter(form => form.hasResponded);
  const pendingForms = activeForms.filter(form => !form.hasResponded);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <div className="h-8 w-8 bg-primary-600 rounded-lg flex items-center justify-center">
                <FileText className="h-5 w-5 text-white" />
              </div>
              <h1 className="text-xl font-semibold text-gray-900">Available Forms</h1>
            </div>
            
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">
                Welcome, {currentUser?.name}
              </span>
              <button
                onClick={handleLogout}
                className="flex items-center text-gray-600 hover:text-gray-900"
              >
                <LogOut className="h-4 w-4 mr-2" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

             <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
         {/* User Profile Section */}
         <div className="mb-8">
           <UserProfile />
         </div>
         
         {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <div className="flex items-center">
              <div className="h-12 w-12 bg-blue-100 rounded-lg flex items-center justify-center">
                <FileText className="h-6 w-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Available Forms</p>
                <p className="text-2xl font-semibold text-gray-900">{activeForms.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <div className="flex items-center">
              <div className="h-12 w-12 bg-green-100 rounded-lg flex items-center justify-center">
                <CheckCircle className="h-6 w-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed</p>
                <p className="text-2xl font-semibold text-gray-900">{completedForms.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <div className="flex items-center">
              <div className="h-12 w-12 bg-yellow-100 rounded-lg flex items-center justify-center">
                <Clock className="h-6 w-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-semibold text-gray-900">{pendingForms.length}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Forms */}
        <div className="mb-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Forms to Complete</h2>
        </div>

        {loading ? (
          <div className="flex justify-center py-12">
            <div className="loading-spinner"></div>
          </div>
        ) : activeForms.length === 0 ? (
          <div className="text-center py-12">
            <div className="mx-auto h-12 w-12 text-gray-400 mb-4">
              <FileText className="h-12 w-12" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No forms available
            </h3>
            <p className="text-gray-600">
              Check back later for new forms to complete
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {activeForms.map((form) => {
              const status = getFormStatus(form);
              
              return (
                <div key={form.id} className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow form-card">
                  <div className="p-6">
                    <div className="flex items-start justify-between mb-4">
                      <h3 className="text-lg font-semibold text-gray-900 line-clamp-2">
                        {form.title}
                      </h3>
                      <div className="flex items-center">
                        {getStatusIcon(status)}
                      </div>
                    </div>

                    {form.description && (
                      <p className="text-gray-600 text-sm mb-4 line-clamp-2">
                        {form.description}
                      </p>
                    )}

                    <div className="flex items-center justify-between text-sm text-gray-500 mb-4">
                      <div className="flex items-center">
                        <Calendar className="h-4 w-4 mr-1" />
                        {formatDate(form.created_at)}
                      </div>
                      <div className="flex items-center">
                        <Users className="h-4 w-4 mr-1" />
                        {form.responseCount} responses
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(status)}`}>
                        {getStatusText(status)}
                      </span>

                      <button
                        onClick={() => navigate(`/user/form/${form.id}`)}
                        disabled={form.hasResponded}
                        className={`flex items-center text-sm font-medium ${
                          form.hasResponded 
                            ? 'text-gray-400 cursor-not-allowed' 
                            : 'text-primary-600 hover:text-primary-700'
                        }`}
                      >
                        {form.hasResponded ? (
                          'Already Completed'
                        ) : (
                          <>
                            Start Form
                            <ArrowRight className="h-4 w-4 ml-1" />
                          </>
                        )}
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
