          // Forms store
          if (!db.objectStoreNames.contains('forms')) {
            const formStore = db.createObjectStore('forms', { keyPath: 'id' });
            formStore.createIndex('created_by', 'created_by');
            formStore.createIndex('is_active', 'is_active');
          }

          // Form responses store
          if (!db.objectStoreNames.contains('form_responses')) {
            const responseStore = db.createObjectStore('form_responses', { keyPath: 'id' });
            responseStore.createIndex('form_id', 'form_id');
            responseStore.createIndex('user_id', 'user_id');
            responseStore.createIndex('form_user', ['form_id', 'user_id'], { unique: true });
          }

          // Password reset tokens store (new in version 2)
          if (!db.objectStoreNames.contains('password_reset_tokens')) {
            const tokenStore = db.createObjectStore('password_reset_tokens', { keyPath: 'id' });
            tokenStore.createIndex('token', 'token', { unique: true });
            tokenStore.createIndex('user_id', 'user_id');
            tokenStore.createIndex('expires_at', 'expires_at');
          }
        }
      });

      await this.seedInitialData();
    } catch (error) {
      console.error('Database initialization error:', error);
    }
  }

  async seedInitialData() {
    try {
      // Check if admin user already exists
      const adminExists = await this.getUserByEmail('admin@example.com');
      
      if (!adminExists) {
        // Create admin user
        const adminPassword = await bcrypt.hash('admin123', 10);
        await this.createUser('admin@example.com', adminPassword, 'Admin User', 'admin');

        // Create demo user
        const userPassword = await bcrypt.hash('user123', 10);
        await this.createUser('user@example.com', userPassword, 'Regular User', 'user');
        await this.createUser('user2@example.com', userPassword, 'Another User', 'user');

        console.log('Initial data seeded successfully');
      }
    } catch (error) {
      console.error('Error seeding initial data:', error);
    }
  }

  // User operations
  async createUser(email, password, name, role = 'user', socialProvider = null, socialId = null) {
    try {
      const userId = uuidv4();
      const user = {
        id: userId,
        email,
        password,
        name,
        role,
        social_provider: socialProvider,
        social_id: socialId,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      };

      await this.db.add('users', user);
      return { success: true, userId };
    } catch (error) {
      if (error.name === 'ConstraintError') {
        throw new Error('User already exists');
      }
      throw error;
    }
  }

  async authenticateUser(email, password) {
    try {
      const user = await this.getUserByEmail(email);
      
      if (!user) {
        return null;
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      
      if (!isValidPassword) {
        return null;
      }

      const { password: _, ...userWithoutPassword } = user;
      return userWithoutPassword;
    } catch (error) {
      console.error('Authentication error:', error);
      return null;
    }
  }

  async getUserByEmail(email) {
    try {
      const tx = this.db.transaction('users', 'readonly');
      const store = tx.objectStore('users');
      const index = store.index('email');
      return await index.get(email);
    } catch (error) {
      console.error('Get user by email error:', error);
      return null;
    }
  }

  async getUserById(userId) {
    try {
      return await this.db.get('users', userId);
    } catch (error) {
      console.error('Get user error:', error);
      return null;
    }
  }

  async getAllUsers() {
    try {
      return await this.db.getAll('users');
    } catch (error) {
      console.error('Get all users error:', error);
      return [];
    }
  }

  // Password reset operations
  async createPasswordResetToken(email) {
    try {
      const user = await this.getUserByEmail(email);
      if (!user) {
        throw new Error('User not found');
      }

      // Generate a secure token
      const token = uuidv4();
      const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 hours from now

      const resetToken = {
        id: uuidv4(),
        token,
        user_id: user.id,
        expires_at: expiresAt.toISOString(),
        created_at: new Date().toISOString()
      };

      await this.db.add('password_reset_tokens', resetToken);
      return { success: true, token, user };
    } catch (error) {
      console.error('Create password reset token error:', error);
      throw error;
    }
  }

  async validatePasswordResetToken(token) {
    try {
      const tx = this.db.transaction('password_reset_tokens', 'readonly');
      const store = tx.objectStore('password_reset_tokens');
      const index = store.index('token');
      const resetToken = await index.get(token);

      if (!resetToken) {
        return null;
      }

      // Check if token is expired
      const expiresAt = new Date(resetToken.expires_at);
      if (expiresAt < new Date()) {
        // Delete expired token
        await this.deletePasswordResetToken(token);
        return null;
      }

      return resetToken;
    } catch (error) {
      console.error('Validate password reset token error:', error);
      return null;
    }
  }

  async resetPassword(token, newPassword) {
    try {
      const resetToken = await this.validatePasswordResetToken(token);
      if (!resetToken) {
        throw new Error('Invalid or expired token');
      }

      // Hash the new password
      const hashedPassword = await bcrypt.hash(newPassword, 10);

      // Update user password
      const user = await this.getUserById(resetToken.user_id);
      if (!user) {
        throw new Error('User not found');
      }

      const updatedUser = {
        ...user,
        password: hashedPassword,
        updated_at: new Date().toISOString()
      };

      await this.db.put('users', updatedUser);

      // Delete the used token
      await this.deletePasswordResetToken(token);

      return { success: true, user: updatedUser };
    } catch (error) {
      console.error('Reset password error:', error);
      throw error;
    }
  }

  async deletePasswordResetToken(token) {
    try {
      const tx = this.db.transaction('password_reset_tokens', 'readonly');
      const store = tx.objectStore('password_reset_tokens');
      const index = store.index('token');
      const resetToken = await index.get(token);

      if (resetToken) {
        await this.db.delete('password_reset_tokens', resetToken.id);
      }
    } catch (error) {
      console.error('Delete password reset token error:', error);
    }
  }

