    try {
      const tx = this.db.transaction('password_reset_tokens', 'readonly');
      const store = tx.objectStore('password_reset_tokens');
      const index = store.index('expires_at');
      const allTokens = await index.getAll();

      const now = new Date();
      const expiredTokens = allTokens.filter(token => new Date(token.expires_at) < now);

      for (const token of expiredTokens) {
        await this.db.delete('password_reset_tokens', token.id);
      }

      return expiredTokens.length;
    } catch (error) {
      console.error('Cleanup expired tokens error:', error);
      return 0;
    }
  }

  // Form operations
  async createForm(formData, createdBy) {
    try {
      const formId = uuidv4();
      const form = {
        id: formId,
        title: formData.title,
        description: formData.description,
        fields: formData.fields,
        is_active: true,
        created_by: createdBy,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      };

      await this.db.add('forms', form);
      return { success: true, formId };
    } catch (error) {
