      return { success: true, responseId };
    } catch (error) {
      if (error.name === 'ConstraintError') {
        throw new Error('You have already responded to this form');
      }
      console.error('Submit response error:', error);
      throw error;
    }
  }

  async getFormResponses(formId) {
    try {
      const tx = this.db.transaction('form_responses', 'readonly');
      const store = tx.objectStore('form_responses');
      const index = store.index('form_id');
      const responses = await index.getAll(formId);

      // Get user details for each response
      const responsesWithUserData = await Promise.all(
        responses.map(async (response) => {
          const user = await this.getUserById(response.user_id);
          return {
            ...response,
            user_name: user?.name || `User ${response.user_id}`,
            user_email: user?.email || ''
          };
        })
      );

      return responsesWithUserData.sort((a, b) => 
        new Date(b.submitted_at) - new Date(a.submitted_at)
      );
    } catch (error) {
      console.error('Get form responses error:', error);
      return [];
    }
  }

  async getUserResponses(userId) {
    try {
      const tx = this.db.transaction('form_responses', 'readonly');
      const store = tx.objectStore('form_responses');
      const index = store.index('user_id');
      const responses = await index.getAll(userId);

      // Get form details for each response
      const responsesWithFormData = await Promise.all(
        responses.map(async (response) => {
          const form = await this.getFormById(response.form_id);
          return {
            ...response,
            form_title: form?.title || 'Unknown Form'
          };
        })
      );

      return responsesWithFormData.sort((a, b) => 
        new Date(b.submitted_at) - new Date(a.submitted_at)
      );
    } catch (error) {
      console.error('Get user responses error:', error);
      return [];
    }
  }

  async hasUserResponded(formId, userId) {
    try {
      const tx = this.db.transaction('form_responses', 'readonly');
      const store = tx.objectStore('form_responses');
      const index = store.index('form_user');
      const response = await index.get([formId, userId]);
      return !!response;
    } catch (error) {
      console.error('Check user response error:', error);
      return false;
    }
  }

  async getFormResponseCount(formId) {
    try {
      const tx = this.db.transaction('form_responses', 'readonly');
      const store = tx.objectStore('form_responses');
      const index = store.index('form_id');
      const responses = await index.getAll(formId);
      return responses.length;
    } catch (error) {
      console.error('Get response count error:', error);
      return 0;
    }
  }

  // Statistics
  async getStatistics() {
    try {
      const [users, forms, responses] = await Promise.all([
        this.getAllUsers(),
        this.getAllForms(),
        this.db.getAll('form_responses')
      ]);

      const activeForms = forms.filter(form => form.is_active);

      return {
        totalUsers: users.length,
        totalForms: forms.length,
        totalResponses: responses.length,
        activeForms: activeForms.length
      };
    } catch (error) {
      console.error('Get statistics error:', error);
      return { totalUsers: 0, totalForms: 0, totalResponses: 0, activeForms: 0 };
    }
  }

  // Close database connection
  close() {
    if (this.db) {
      this.db.close();
    }
  }
}

