import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useForm } from '../../contexts/FormContext';
import { useAuth } from '../../contexts/AuthContext';
import FormField from '../form/FormField';
import { 
  ArrowLeft, 
  Send, 
  CheckCircle,
  AlertCircle
} from 'lucide-react';

const FormResponse = () => {
  const { formId } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { getFormById, submitResponse, hasUserResponded, loading } = useForm();
  
  const [formData, setFormData] = useState(null);
  const [formValues, setFormValues] = useState({});
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [hasAlreadyResponded, setHasAlreadyResponded] = useState(false);
  const [formLoading, setFormLoading] = useState(true);

  useEffect(() => {
    loadFormData();
  }, [formId, loadFormData]);

  const loadFormData = async () => {
    try {
      setFormLoading(true);
      const form = await getFormById(formId);
      
      if (form) {
        setFormData(form);
        
        // Check if user has already responded
        const responded = await hasUserResponded(formId, currentUser.id);
        setHasAlreadyResponded(responded);
        
        // Initialize form values
        const initialValues = {};
        form.fields.forEach((field, index) => {
          const fieldId = field.id || index;
          if (field.type === 'checkbox') {
            initialValues[fieldId] = [];
          } else {
            initialValues[fieldId] = '';
          }
        });
        setFormValues(initialValues);
      }
    } catch (error) {
      console.error('Error loading form data:', error);
    } finally {
      setFormLoading(false);
    }
  };

  const handleFieldChange = (index, value) => {
    const fieldId = formData.fields[index].id || index;
    setFormValues(prev => ({
      ...prev,
      [fieldId]: value
    }));
    
    // Clear error for this field
    if (errors[fieldId]) {
      setErrors(prev => ({
        ...prev,
        [fieldId]: null
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    formData.fields.forEach((field, index) => {
      const fieldId = field.id || index;
      const value = formValues[fieldId];
      
      if (field.required) {
        if (!value || (Array.isArray(value) && value.length === 0) || value.trim() === '') {
          newErrors[fieldId] = 'This field is required';
        }
      }
      
      // Additional validation for email fields
      if (field.type === 'email' && value && !isValidEmail(value)) {
        newErrors[fieldId] = 'Please enter a valid email address';
      }
    });
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setIsSubmitting(true);
    
    try {
      const result = await submitResponse(formId, formValues, currentUser.id);
      if (result.success) {
        // Redirect to user dashboard after successful submission
        navigate('/user');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  if (formLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  if (!formData.id) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Form not found</h2>
          <button
            onClick={() => navigate('/user')}
            className="text-primary-600 hover:text-primary-700"
          >
            Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  if (hasAlreadyResponded) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8 text-center">
            <div className="mx-auto h-16 w-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">
              Form Already Completed
            </h2>
            <p className="text-gray-600 mb-6">
              You have already submitted a response for "{formData.title}". Each user can only respond once to this form.
            </p>
            <button
              onClick={() => navigate('/user')}
              className="inline-flex items-center px-4 py-2 text-sm font-medium text-white bg-primary-600 border border-transparent rounded-md hover:bg-primary-700"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Dashboard
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/user')}
                className="flex items-center text-gray-600 hover:text-gray-900"
              >
                <ArrowLeft className="h-5 w-5 mr-2" />
                Back to Dashboard
              </button>
              <h1 className="text-xl font-semibold text-gray-900">
                {formData.title}
              </h1>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-8">
            {/* Form Header */}
            <div className="mb-8">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">
                {formData.title}
              </h2>
              {formData.description && (
                <p className="text-gray-600 text-lg mb-4">
                  {formData.description}
                </p>
              )}
              <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                <div className="flex items-start">
                  <AlertCircle className="h-5 w-5 text-blue-600 mt-0.5 mr-3 flex-shrink-0" />
                  <div className="text-sm text-blue-800">
                    <p className="font-medium mb-1">Important:</p>
                    <ul className="list-disc list-inside space-y-1">
                      <li>You can only submit this form once</li>
                      <li>Required fields are marked with an asterisk (*)</li>
                      <li>Please review your answers before submitting</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>

            {/* Form Fields */}
            <form onSubmit={handleSubmit} className="space-y-6">
              {formData.fields.map((field, index) => {
                const fieldId = field.id || index;
                const hasError = errors[fieldId];
                
                return (
                  <div key={fieldId} className={`${hasError ? 'border-l-4 border-red-400 pl-4' : ''}`}>
                    <FormField
                      field={field}
                      index={index}
                      isPreview={true}
                      value={formValues[fieldId] || ''}
                      onChange={(value) => handleFieldChange(index, value)}
                    />
                    {hasError && (
                      <p className="mt-1 text-sm text-red-600 flex items-center">
                        <AlertCircle className="h-4 w-4 mr-1" />
                        {hasError}
                      </p>
                    )}
                  </div>
                );
              })}

              {/* Submit Button */}
              <div className="pt-6 border-t border-gray-200">
                <button
                  type="submit"
                  disabled={isSubmitting || loading}
                  className="w-full flex items-center justify-center px-6 py-3 text-base font-medium text-white bg-primary-600 border border-transparent rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {isSubmitting || loading ? (
                    <>
                      <div className="loading-spinner mr-2"></div>
                      Submitting...
                    </>
                  ) : (
                    <>
                      <Send className="h-5 w-5 mr-2" />
                      Submit Form
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FormResponse;
