import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Chrome, Facebook } from 'lucide-react';

const SocialLogin = () => {
  const [isLoading, setIsLoading] = useState(false);
  const { loginWithSocial } = useAuth();

  const handleSocialLogin = async (provider) => {
    setIsLoading(true);
    
    try {
      // Simulate social login for demo purposes
      const mockUser = {
        email: `demo-${provider}@example.com`,
        name: `Demo ${provider.charAt(0).toUpperCase() + provider.slice(1)} User`,
        socialProvider: provider,
        socialId: `demo-${provider}-123`
      };
      
      await loginWithSocial(mockUser);
    } catch (error) {
      console.error('Social login error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-3">
      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <div className="w-full border-t border-gray-300" />
        </div>
        <div className="relative flex justify-center text-sm">
          <span className="px-2 bg-white text-gray-500">Ou continue com</span>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-3">
        <button
          onClick={() => handleSocialLogin('google')}
          disabled={isLoading}
          className="w-full inline-flex justify-center py-2 px-4 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <Chrome className="h-5 w-5" />
          <span className="ml-2">Google</span>
        </button>

        <button
          onClick={() => handleSocialLogin('facebook')}
          disabled={isLoading}
          className="w-full inline-flex justify-center py-2 px-4 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <Facebook className="h-5 w-5" />
          <span className="ml-2">Facebook</span>
        </button>
      </div>

      {isLoading && (
        <div className="text-center">
          <div className="loading-spinner mx-auto"></div>
          <p className="text-sm text-gray-600 mt-2">Processando login social...</p>
        </div>
      )}
    </div>
  );
};

export default SocialLogin;
