import React, { useEffect, useState, useCallback } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import socialAuthService from '../../services/socialAuth';
import { CheckCircle, AlertCircle } from 'lucide-react';

const OAuthCallback = () => {
  const { provider } = useParams();
  const [searchParams] = useSearchParams();
  const [status, setStatus] = useState('processing');
  const [error, setError] = useState(null);

  const handleOAuthCallback = useCallback(async () => {
    try {
      setStatus('processing');

      // Get parameters from URL
      const accessToken = searchParams.get('access_token');
      const errorParam = searchParams.get('error');
      const errorDescription = searchParams.get('error_description');

      if (errorParam) {
        throw new Error(errorDescription || 'Falha na autenticação OAuth');
      }

      if (!accessToken) {
        throw new Error('Nenhum token de acesso recebido');
      }

      let result;

      // Process the OAuth callback based on provider
      switch (provider) {
        case 'google':
          result = await socialAuthService.handleGoogleCallback(accessToken);
          break;
        case 'facebook':
          result = await socialAuthService.handleFacebookCallback(accessToken);
          break;
        default:
          throw new Error(`Provedor não suportado: ${provider}`);
      }

      if (result.success) {
        // Send success message to parent window
        window.opener.postMessage({
          type: 'OAUTH_SUCCESS',
          payload: result
        }, window.location.origin);

        setStatus('success');

        // Close popup after a short delay
        setTimeout(() => {
          window.close();
        }, 2000);
      } else {
        throw new Error('Falha na autenticação');
      }
    } catch (error) {
      console.error('Erro no callback OAuth:', error);
      setError(error.message);
      setStatus('error');

      // Send error message to parent window
      window.opener.postMessage({
        type: 'OAUTH_ERROR',
        error: error.message
      }, window.location.origin);

      // Close popup after a short delay
      setTimeout(() => {
        window.close();
      }, 3000);
    }
  }, [provider, searchParams]);

  useEffect(() => {
    handleOAuthCallback();
  }, [provider, searchParams, handleOAuthCallback]);

  if (status === 'processing') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="loading-spinner mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold text-gray-900 mb-2">
            Completando Login do {provider}...
          </h2>
          <p className="text-gray-600">
            Por favor, aguarde enquanto completamos sua autenticação.
          </p>
        </div>
      </div>
    );
  }

  if (status === 'success') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-green-100 rounded-full flex items-center justify-center mb-4">
            <CheckCircle className="h-8 w-8 text-green-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            Login Bem-sucedido!
          </h2>
          <p className="text-gray-600 mb-4">
            Sua autenticação foi concluída com sucesso.
          </p>
          <p className="text-sm text-gray-500">
            Esta janela será fechada automaticamente...
          </p>
        </div>
      </div>
    );
  }

  if (status === 'error') {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="mx-auto h-16 w-16 bg-red-100 rounded-full flex items-center justify-center mb-4">
            <AlertCircle className="h-8 w-8 text-red-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            Erro na Autenticação
          </h2>
          <p className="text-gray-600 mb-4">
            {error || 'Ocorreu um erro durante a autenticação.'}
          </p>
          <p className="text-sm text-gray-500">
            Esta janela será fechada automaticamente...
          </p>
        </div>
      </div>
    );
  }

  return null;
};

export default OAuthCallback;
