import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import toast from 'react-hot-toast';
import apiService from '../services/api';

const FormContext = createContext();

export const useForm = () => {
  const context = useContext(FormContext);
  if (!context) {
    throw new Error('useForm must be used within a FormProvider');
  }
  return context;
};

export const FormProvider = ({ children }) => {
  const [forms, setForms] = useState([]);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(false);

  // Load data from database on mount
  useEffect(() => {
    loadData();
  }, []);

  const loadData = useCallback(async () => {
    try {
      const response = await apiService.getAllForms();
      if (response.success) {
        setForms(response.forms);
        // Responses will be loaded per form when needed
        setResponses([]);
      }
    } catch (error) {
      console.error('Error loading data:', error);
    }
  }, []);

  const createForm = useCallback(async (formData) => {
    setLoading(true);
    try {
      const response = await apiService.createForm(formData);
      
      if (response.success) {
        // Reload forms to get the updated list
        const updatedResponse = await apiService.getAllForms();
        if (updatedResponse.success) {
          setForms(updatedResponse.forms);
        }
        toast.success('Form created successfully!');
        return { success: true, formId: response.form.id };
      }
    } catch (error) {
      toast.error('Failed to create form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  }, []);

  const updateForm = useCallback(async (formId, formData) => {
    setLoading(true);
    try {
      const response = await apiService.updateForm(formId, formData);
      
      if (response.success) {
        // Reload forms to get the updated list
        const updatedResponse = await apiService.getAllForms();
        if (updatedResponse.success) {
          setForms(updatedResponse.forms);
        }
        
        toast.success('Form updated successfully!');
        return { success: true };
      }
    } catch (error) {
      toast.error('Failed to update form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  }, []);

  const deleteForm = useCallback(async (formId) => {
    setLoading(true);
    try {
      const response = await apiService.deleteForm(formId);
      
      if (response.success) {
        // Reload forms to get the updated list
        const updatedResponse = await apiService.getAllForms();
        if (updatedResponse.success) {
          setForms(updatedResponse.forms);
        }
        
        toast.success('Form deleted successfully!');
        return { success: true };
      }
    } catch (error) {
      toast.error('Failed to delete form');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  }, []);

  const submitResponse = useCallback(async (formId, responseData, userId) => {
    setLoading(true);
    try {
      const response = await apiService.submitFormResponse(formId, responseData);
      
      if (response.success) {
        toast.success('Response submitted successfully!');
        return { success: true };
      }
    } catch (error) {
      toast.error(error.message || 'Failed to submit response');
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  }, []);

  const getFormById = useCallback(async (formId) => {
    try {
      const response = await apiService.getFormById(formId);
      return response.success ? response.form : null;
    } catch (error) {
      console.error('Error getting form by ID:', error);
      return null;
    }
  }, []);

  const getFormResponses = useCallback(async (formId) => {
    try {
      const response = await apiService.getFormResponses(formId);
      return response.success ? response.responses : [];
    } catch (error) {
      console.error('Error getting form responses:', error);
      return [];
    }
  }, []);

  const getUserResponses = useCallback(async (userId) => {
    try {
      const response = await apiService.getUserResponses();
      return response.success ? response.responses : [];
    } catch (error) {
      console.error('Error getting user responses:', error);
      return [];
    }
  }, []);

  const getActiveForms = useCallback(async () => {
    try {
      const response = await apiService.getActiveForms();
      return response.success ? response.forms : [];
    } catch (error) {
      console.error('Error getting active forms:', error);
      return [];
    }
  }, []);

  const getFormResponseCount = useCallback(async (formId) => {
    try {
      const form = await getFormById(formId);
      return form ? form.responseCount : 0;
    } catch (error) {
      console.error('Error getting form response count:', error);
      return 0;
    }
  }, [getFormById]);

  const hasUserResponded = async (formId, userId) => {
    try {
      const form = await getFormById(formId);
      return form ? form.hasResponded : false;
    } catch (error) {
      console.error('Error checking user response:', error);
      return false;
    }
  };

  const getStatistics = async () => {
    try {
      const response = await apiService.getStatistics();
      return response.success ? response.stats : { totalUsers: 0, totalForms: 0, totalResponses: 0, activeForms: 0 };
    } catch (error) {
      console.error('Error getting statistics:', error);
      return { totalUsers: 0, totalForms: 0, totalResponses: 0, activeForms: 0 };
    }
  };

  const value = {
    forms,
    responses,
    loading,
    createForm,
    updateForm,
    deleteForm,
    submitResponse,
    getFormById,
    getFormResponses,
    getUserResponses,
    getActiveForms,
    getFormResponseCount,
    hasUserResponded,
    getStatistics,
    loadData
  };

  return (
    <FormContext.Provider value={value}>
      {children}
    </FormContext.Provider>
  );
};
